mapper.select:
SELECT cid,course_name,course_credit,create_time FROM studentms.t_course
where delete_status =1

    and cid = #{course.courseId}



        AND course_name = #{course.courseName}


        AND course_name LIKE CONCAT('%', #{course.courseName}, '%')



    AND course_credit >= #{lowBound}


    AND #{highBound} >= course_credit

mapper.update:
UPDATE studentms.t_course
SET course_name   = #{course.courseName},
    course_credit = #{course.courseCredit}
WHERE cid = #{course.courseId}
  and delete_status = 1
