mapper.select:
SELECT * FROM studentms.ct


        cid = #{cid}


        AND tid = #{tid}


        AND term = #{term}

SELECT c.cid, c.cname, c.ccredit
FROM studentms.c c
         INNER JOIN studentms.ct ct ON c.cid = ct.cid
WHERE ct.tid = #{tid}
  AND ct.term = #{term}

SELECT c.cid, c.course_name, t.tid, t.teacher_name, c.course_credit, ct.ctid
FROM studentms.t_course c INNER JOIN studentms.t_course_teacher ct ON c.cid = ct.cid
INNER JOIN studentms.t_teacher t ON ct.tid = t.tid
where ct.delete_status =1

       and  t.tid = #{teacherId}



            AND t.teacher_name = #{teacherName}


            AND t.teacher_name LIKE CONCAT('%', #{teacherName}, '%')



        AND c.cid = #{courseId}



            AND c.course_name = #{courseName}


            AND c.course_name LIKE CONCAT('%', #{courseName}, '%')
