mapper.select:
SELECT c.cid, c.course_name, t.tid, t.teacher_name, sct.grade, c.course_credit, sct.sctid,sct.create_time
FROM studentms.t_score_course_teacher sct
         INNER JOIN studentms.t_teacher t ON sct.tid = t.tid
         INNER JOIN studentms.t_course c ON sct.cid = c.cid
WHERE sct.sid = #{studentId}
  AND sct.term = #{term}

SELECT c.cid, c.cname, t.tid, t.tname, s.sid, s.sname, sct.grade, sct.term
FROM studentms.t_score_course_teacher sct INNER JOIN studentms.t_student s ON sct.sid = s.sid
INNER JOIN studentms.t t ON sct.tid = t.tid
INNER JOIN studentms.c c ON sct.cid = c.cid


        s.sid = #{sid}


        AND t.tid = #{tid}


        AND c.cid = #{cid}



            AND s.sname = #{sname}


            AND s.sname LIKE CONCAT('%', #{sname}, '%')




            AND t.tname = #{tname}


            AND t.tname LIKE CONCAT('%', #{tname}, '%')




            AND c.cname = #{cname}


            AND c.cname LIKE CONCAT('%', #{cname}, '%')



        AND sct.term = #{term}


        AND sct.grade >= #{lowBound}


        AND #{highBound} >= sct.grade
